let popupEl=document.getElementById("popup"),clickCountEl=document.getElementById("click-count"),resetButtonEl=document.getElementById("reset"),clicks=0|localStorage.getItem("clicks");function show(e){e.classList.remove("hidden")}function hide(e){e.classList.add("hidden")}function openPopup(){clicks++,localStorage.setItem("clicks",clicks),updatePopup(),show(popupEl)}function closePopup(){hide(popupEl)}function updatePopup(){clickCountEl.innerText=clicks+" "+(1==clicks?"time":"times"),console.log(clicks),(5<=clicks?show:hide)(resetButtonEl)}function resetClicks(){localStorage.removeItem("clicks"),clicks=0,updatePopup()}